mapper.delete:

    UPDATE shopdb.board 
       SET hit = hit + 1
     WHERE wno = #{wno}

mapper.select:
	SELECT wno, title, content, writer, hit, cast( DATE_FORMAT( wdate, '%Y-%m-%d %H:%i:%s' ) AS CHAR(19) ) AS wdate
    FROM shopdb.board 
    WHERE wno = #{wno}

mapper.delete:
    
		UPDATE shopdb.board
		SET title = #{title}, content = #{content}, wdate = dateNow
		WHERE wno = #{wno}
